def wnid_tree_son_father():
	'''
	Download wn Hierarchy at http://www.image-net.org/archive/wordnet.is_a.txt
	Convert "father_wnid son_wnid" to "son_wnid father_wnid" 
	'''
	with open("wnid_tree_father_son.txt", 'r') as fp:
		lines = fp.read().splitlines()
	new_lines = []
	for line in lines:
		father, son = line.split(' ')
		new_lines.append(son + ' ' + father)
	print(len(lines), lines[0:5])
	print(len(new_lines), new_lines[0:5])

	with open("wnid_tree_son_father.txt", 'w+') as fp:
		for new_line in new_lines:
			fp.write(new_line+'\n')


