def imagenetA_classes():
	'''
	Generate two json files: one for selected class index and the other for selected class names.
	'''
	with open('imgnetid2word.json', 'r') as fp:
		imgnetid2word = json.load(fp)
	with open('imagenetA_200_labels.json', 'r') as fp:
		thousand_k_to_200 = json.load(fp)
	imagenetA_keywords = [imgnetid2word[k] for k in thousand_k_to_200 if thousand_k_to_200[k] != -1]
