def imgnetid2wnid():
	'''
	imgnetid2wnid and wnid2imgnetid
	see https://gist.github.com/fnielsen/4a5c94eaa6dcdf29b7a62d886f540372#gistcomment-2370970
	'''
	# import tensorflow as tf
	# mapping = tf.keras.applications.densenet.decode_predictions(np.array([range(1000)]), top=1000)[0]
	# print(mapping)
	# np.save('imgnetid2wnid.npy', np.array(mapping))

	mapping = np.load('imgnetid2wnid.npy')
	print(mapping.shape)

	C = mapping.shape[0]
	assert C==1000 # 1000 class for imagenet

	imgnetid2wnid = {}
	wnid2imgnetid = {}
	for i in range(C):
		wnid, part_word, imgnetid = mapping[i, :] # all type=str
		imgnetid2wnid[imgnetid] = wnid
		wnid2imgnetid[wnid] = imgnetid
		if i % 100 == 0: # just to check
			print(wnid, part_word, imgnetid)
			
	with open('imgnetid2wnid.json', 'w+') as fp:
		json.dump(imgnetid2wnid, fp, indent=4)

	with open('wnid2imgnetid.json', 'w+') as fp:
		json.dump(wnid2imgnetid, fp, indent=4)

def wnid2word():
	'''
	Donwload wnid txt file from http://image-net.org/archive/words.txt,
	and convert it to json file.
	'''
	
	with open('all_noun_wnid.txt') as f:
		wnid_key = f.readlines()
	# you may also want to remove whitespace characters like `\n` at the end of each line
	wnid_key = [x.strip() for x in wnid_key] 
	print('wnid_key:', type(wnid_key), len(wnid_key))
	
	wnid2word = {}
	for i in range(len(wnid_key)):
		wnid = wnid_key[i][0:9]
		key = wnid_key[i][10:]
		wnid2word[wnid] = key

	with open('wnid2word.json', 'w+') as fp:
		json.dump(wnid2word, fp, indent=4)
	
def imgnetid_wnid_word_gloss():
	'''
	A csv file with four columns.
	'''
	# load .mat file:
	from scipy.io import loadmat
	meta = loadmat('meta.mat')['synsets']
	print(type(meta), meta.shape, meta[0,0])

	# read wnid2imgnetid:
	with open('wnid2imgnetid.json') as fp:
		wnid2imgnetid = json.load(fp)

	# construct csv:
	imgnetid_lst, wnid_lst, word_lst, gloss_lst = [], [], [], []
	for i in range(1000): # only the first 
		_, wnid, word, gloss, _, _, _, _ = meta[i,0]
		wnid, word, gloss = wnid[0], word[0], gloss[0]
		imgnetid = int(wnid2imgnetid[wnid])
		wnid_lst.append(wnid)
		word_lst.append(word)
		gloss_lst.append(gloss)
		imgnetid_lst.append(imgnetid)

	imgnetid_wnid_word_gloss = pd.DataFrame({
		'imgnetid': imgnetid_lst,
		'wnid': wnid_lst,
		'word': word_lst,
		'gloss': gloss_lst,
	}, columns=['imgnetid', 'wnid', 'word', 'gloss']).sort_values(by=['imgnetid'], ascending=True) 

	imgnetid_wnid_word_gloss.to_csv("imgnetid_wnid_word_gloss.csv", index=False)
		 
	


